    }),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  // Filter tasks by status
  const todoTasks = tasks.filter(task => task.status === 'todo')
    .sort((a, b) => a.position - b.position);

  const inProgressTasks = tasks.filter(task => task.status === 'in-progress')
    .sort((a, b) => a.position - b.position);

  const doneTasks = tasks.filter(task => task.status === 'done')
    .sort((a, b) => a.position - b.position);

  // Filter tasks by category if selected
  const filteredTodoTasks = selectedCategory 
    ? todoTasks.filter(task => task.category === selectedCategory)
    : todoTasks;

  const filteredInProgressTasks = selectedCategory
    ? inProgressTasks.filter(task => task.category === selectedCategory)
    : inProgressTasks;

  const filteredDoneTasks = selectedCategory
    ? doneTasks.filter(task => task.category === selectedCategory)
    : doneTasks;

  // Get task IDs for each column
  const todoIds = filteredTodoTasks.map(task => task.id);
  const inProgressIds = filteredInProgressTasks.map(task => task.id);
  const doneIds = filteredDoneTasks.map(task => task.id);

  // tRPC mutations
  const utils = api.useUtils();

  const updateTaskMutation = api.tasks.update.useMutation({
    onSuccess: () => {
      utils.tasks.list.invalidate();
    },
  });

  const updatePositionsMutation = api.tasks.updatePositions.useMutation({
    onSuccess: () => {
      utils.tasks.list.invalidate();
    },
  });

  const deleteTaskMutation = api.tasks.delete.useMutation({
    onSuccess: () => {
      utils.tasks.list.invalidate();
    },
  });

  // Event handlers
  const handleDragStart = (event: DragStartEvent) => {
    const { active } = event;
    setActiveId(active.id as string);

    const draggedTask = tasks.find(task => task.id === active.id);
    if (draggedTask) {
      setActiveTask(draggedTask);
    }
  };

  const handleDragOver = (event: DragOverEvent) => {
    const { active, over } = event;

    if (!over || active.id === over.id) {
      return;
    }

    // Find the containers (columns)
    const activeContainer = findContainer(active.id as string);
    const overContainer = findContainer(over.id as string);

    if (!activeContainer || !overContainer) {
      return;
    }

    // If same container, we don't need to do anything here
    if (activeContainer === overContainer) {
      return;
    }

    // Moving to a different container
    setTasks(prev => {
      const activeIndex = findTaskIndex(prev, active.id as string);
      const overIndex = findTaskIndex(prev, over.id as string);

      if (activeIndex === -1) {
        return prev;
      }

      // Update the task's status based on the container it's moved to
      const updatedTasks = [...prev];
      const movedTask = { ...updatedTasks[activeIndex] };

      // Set the new status based on the container
      movedTask.status = getStatusFromContainer(overContainer);

      // Remove from original position and insert at new position
      updatedTasks.splice(activeIndex, 1);
      updatedTasks.splice(overIndex, 0, movedTask);

      // Update positions for all tasks in the affected containers
      return updatePositionsInContainers(updatedTasks, [activeContainer, overContainer]);
    });
  };

  const handleDragEnd = (event: DragEndEvent) => {
    const { active, over } = event;

    if (!over) {
      setActiveId(null);
      setActiveTask(null);
      return;
    }

    // Find the containers
    const activeContainer = findContainer(active.id as string);
    const overContainer = findContainer(over.id as string);

    if (!activeContainer || !overContainer) {
      setActiveId(null);
      setActiveTask(null);
      return;
    }

    // If moving within the same container
    if (activeContainer === overContainer) {
      const activeIndex = findTaskIndex(tasks, active.id as string);
      const overIndex = findTaskIndex(tasks, over.id as string);

      if (activeIndex !== overIndex) {
        setTasks(prev => {
          const updatedTasks = arrayMove(prev, activeIndex, overIndex);

          // Update positions for all tasks in the container
          return updatePositionsInContainer(updatedTasks, activeContainer);
        });

        // Prepare tasks for position update
        const tasksToUpdate = getTasksInContainer(tasks, activeContainer)
          .sort((a, b) => a.position - b.position)
          .map((task, index) => ({
            id: task.id,
            position: index + 1,
          }));

        if (tasksToUpdate.length > 0) {
          // Update positions in the database - pass array directly
          updatePositionsMutation.mutate(tasksToUpdate);
        }
      }
    } else {
      // If the task was moved to a different container, update its status in the database
      const taskId = active.id as string;
      const task = tasks.find(t => t.id === taskId);

      if (task) {
        const newStatus = getStatusFromContainer(overContainer);

        // Update the task status in the database
        updateTaskMutation.mutate({
          id: taskId,
          status: newStatus,
        });

        // Update positions for both containers
        const activeContainerTasks = getTasksInContainer(tasks, activeContainer)
          .sort((a, b) => a.position - b.position)
          .map((task, index) => ({
            id: task.id,
            position: index + 1,
          }));

        const overContainerTasks = getTasksInContainer(tasks, overContainer)
          .sort((a, b) => a.position - b.position)
          .map((task, index) => ({
            id: task.id,
            position: index + 1,
          }));

        const tasksToUpdate = [...activeContainerTasks, ...overContainerTasks];
        if (tasksToUpdate.length > 0) {
          // Update positions in the database - pass array directly
          updatePositionsMutation.mutate(tasksToUpdate);
        }
      }
    }

    setActiveId(null);
    setActiveTask(null);
  };

  // Helper functions
  const findContainer = (id: string) => {
    if (todoIds && todoIds.includes(id)) return 'todo';
    if (inProgressIds && inProgressIds.includes(id)) return 'in-progress';
    if (doneIds && doneIds.includes(id)) return 'done';
    return null;
  };

  const findTaskIndex = (tasks: Task[], id: string) => {
    if (!tasks || !Array.isArray(tasks)) return -1;
    return tasks.findIndex(task => task && task.id === id);
  };

  const getStatusFromContainer = (containerId: string): TaskStatus => {
    switch (containerId) {
      case 'todo': return TaskStatus.TODO;
      case 'in-progress': return TaskStatus.IN_PROGRESS;
      case 'done': return TaskStatus.DONE;
      default: return TaskStatus.TODO;
    }
  };

  const getContainerFromStatus = (status: TaskStatus): string => {
    switch (status) {
      case TaskStatus.TODO: return 'todo';
      case TaskStatus.IN_PROGRESS: return 'in-progress';
      case TaskStatus.DONE: return 'done';
      default: return 'todo';
    }
  };

  const getTasksInContainer = (tasks: Task[], container: string) => {
    if (!tasks || !Array.isArray(tasks)) return [];
    return tasks.filter(task => task && task.status === getStatusFromContainer(container));
  };

  const updatePositionsInContainer = (tasks: Task[], container: string) => {
    const containerTasks = getTasksInContainer(tasks, container);
    const otherTasks = tasks.filter(task => task.status !== getStatusFromContainer(container));

    // Update positions for tasks in the container
    const updatedContainerTasks = containerTasks
      .sort((a, b) => {
        const aIndex = findTaskIndex(tasks, a.id);
        const bIndex = findTaskIndex(tasks, b.id);
        return aIndex - bIndex;
      })
      .map((task, index) => ({
        ...task,
        position: index + 1,
      }));

    return [...otherTasks, ...updatedContainerTasks];
  };

  const updatePositionsInContainers = (tasks: Task[], containerIds: string[]) => {
    const updatedTasks = [...tasks];

    // For each container, update the positions of its tasks
    containerIds.forEach(containerId => {
      const containerTasks = getTasksInContainer(updatedTasks, containerId);
      const sortedContainerTasks = [...containerTasks].sort((a, b) => a.position - b.position);

      // Update positions
      sortedContainerTasks.forEach((task, index) => {
        const taskIndex = updatedTasks.findIndex(t => t.id === task.id);
        if (taskIndex !== -1) {
          updatedTasks[taskIndex] = {
            ...updatedTasks[taskIndex],
            position: index + 1
          };
        }
      });
      if (containerId) {
        // Use the existing container tasks that were already sorted
        // No need to call updatePositionsInContainer again
      }
    });

    return updatedTasks;
  };

  // Task action handlers
  const handleEditTask = (taskId: string) => {
    if (!taskId) return;
    // This will be handled by the parent component via props
    console.log('Edit task:', taskId);
  };

  const handleDeleteTask = (taskId: string) => {
    if (!taskId) return;

    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        deleteTaskMutation.mutate({ id: taskId });

        // Optimistic update
        setTasks(prev => prev.filter(task => task && task.id !== taskId));
      } catch (error) {
        console.error('Error deleting task:', error);
      }
    }
  };

  const handleStatusChange = (taskId: string, newStatus: TaskStatus) => {
    if (!taskId || !newStatus) return;

    try {
      // Update task status
      updateTaskMutation.mutate({
        id: taskId,
        status: newStatus,
      });

      // Optimistic update
      setTasks(prev => {
        const taskIndex = prev.findIndex(task => task && task.id === taskId);
        if (taskIndex === -1) return prev;

        const updatedTasks = [...prev];
        updatedTasks[taskIndex] = {
          ...updatedTasks[taskIndex],
          status: newStatus,
        };

        return updatePositionsInContainers(updatedTasks, ['todo', 'in-progress', 'done']);
      });
    } catch (error) {
      console.error('Error updating task status:', error);
    }
  };

  return (
    <DndContext
      sensors={sensors}
      collisionDetection={closestCorners}
      onDragStart={handleDragStart}
      onDragOver={handleDragOver}
      onDragEnd={handleDragEnd}
    >
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {/* To Do Column */}
        <TaskColumn 
          title="To Do" 
          tasks={filteredTodoTasks} 
          status="todo"
        >
          <SortableContext items={todoIds} strategy={verticalListSortingStrategy}>
            {filteredTodoTasks.map(task => (
              <TaskCard
                key={task.id}
                task={task}
                onEdit={handleEditTask}
                onDelete={handleDeleteTask}
                onStatusChange={handleStatusChange}
              />
            ))}
          </SortableContext>
        </TaskColumn>

        {/* In Progress Column */}
        <TaskColumn 
          title="In Progress" 
          tasks={filteredInProgressTasks} 
          status="in-progress"
        >
          <SortableContext items={inProgressIds} strategy={verticalListSortingStrategy}>
            {filteredInProgressTasks.map(task => (
              <TaskCard
                key={task.id}
                task={task}
                onEdit={handleEditTask}
                onDelete={handleDeleteTask}
                onStatusChange={handleStatusChange}
              />
            ))}
          </SortableContext>
        </TaskColumn>

        {/* Done Column */}
        <TaskColumn 
          title="Done" 
          tasks={filteredDoneTasks} 
          status="done"
        >
          <SortableContext items={doneIds} strategy={verticalListSortingStrategy}>
            {filteredDoneTasks.map(task => (
              <TaskCard
                key={task.id}
                task={task}
                onEdit={handleEditTask}
                onDelete={handleDeleteTask}
                onStatusChange={handleStatusChange}
              />
            ))}
          </SortableContext>
        </TaskColumn>
      </div>

      {/* Drag Overlay */}
      <DragOverlay>
        {activeId && activeTask ? (
          <TaskCard
            task={activeTask}
            onEdit={handleEditTask}
            onDelete={handleDeleteTask}
            onStatusChange={handleStatusChange}
          />
        ) : null}
      </DragOverlay>
    </DndContext>
  );
}

'use client';

import { useState } from 'react';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { cn } from '@/lib/utils';
import { Card, CardContent, CardFooter, CardHeader } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { 
  MoreHorizontal, 
  Calendar, 
  User, 
  Tag, 
  Edit, 
  Trash2, 
  ArrowUp, 
  ArrowRight, 
  ArrowDown 
} from 'lucide-react';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { TaskStatus, TaskPriority } from '@codexcrm/db/src/models';

// Re-export the enums for other components to use
export { TaskStatus, TaskPriority };

// Define the Task interface
export interface Task {
  id: string;
  title: string;
  description: string | null;
  status: TaskStatus;
  priority: TaskPriority;
  category: string | null;
  color: string | null;
  due_date: string | null;
  contact_id: string | null;
  business_impact: number | null;
  position: number;
  ai_generated: boolean;
  user_id: string;
  created_at?: string;
  updated_at?: string;
}

interface TaskCardProps {
  task: Task;
  onEdit: (taskId: string) => void;
  onDelete: (taskId: string) => void;
  onStatusChange: (taskId: string, newStatus: TaskStatus) => void;
}

export function TaskCard({ task, onEdit, onDelete, onStatusChange }: TaskCardProps) {
  const [isExpanded, setIsExpanded] = useState(false);

  // Set up sortable functionality
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: task.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.5 : 1,
    zIndex: isDragging ? 10 : 1,
  };

  // Priority badge color
  const priorityColors: Record<TaskPriority, string> = {
    [TaskPriority.HIGH]: 'bg-red-500 hover:bg-red-600',
    [TaskPriority.MEDIUM]: 'bg-yellow-500 hover:bg-yellow-600',
    [TaskPriority.LOW]: 'bg-blue-500 hover:bg-blue-600',
    [TaskPriority.NONE]: 'bg-gray-500 hover:bg-gray-600',
  };

  // Status change options based on current status
  const getStatusChangeOptions = () => {
    if (!task || !task.status) return [];

    switch (task.status) {
      case TaskStatus.TODO:
        return [{ label: 'Move to In Progress', value: TaskStatus.IN_PROGRESS }];
      case TaskStatus.IN_PROGRESS:
        return [
          { label: 'Move to To Do', value: TaskStatus.TODO },
          { label: 'Move to Done', value: TaskStatus.DONE },
        ];
      case TaskStatus.DONE:
        return [
          { label: 'Move to To Do', value: TaskStatus.TODO },
          { label: 'Move to In Progress', value: TaskStatus.IN_PROGRESS },
        ];
      default:
        return [];
    }
  };

  // Priority icon
  const PriorityIcon = () => {
    if (!task || !task.priority) return null;

    switch (task.priority) {
      case TaskPriority.HIGH:
        return <ArrowUp className="h-4 w-4 text-red-500" />;
      case TaskPriority.MEDIUM:
        return <ArrowRight className="h-4 w-4 text-yellow-500" />;
      case TaskPriority.LOW:
        return <ArrowDown className="h-4 w-4 text-blue-500" />;
      default:
        return null;
    }
  };

  return (
    <div
      ref={setNodeRef}
      style={style}
      {...attributes}
      className="mb-3"
    >
      <Card className="border-l-4 shadow-sm hover:shadow-md transition-shadow duration-200" 
        style={{ borderLeftColor: task.color || '#e2e8f0' }}>
        <CardHeader className="p-3 pb-0 cursor-grab" {...listeners}>
          <div className="flex justify-between items-start">
            <div className="flex items-center gap-2">
              <PriorityIcon />
              <h3 className="font-medium text-sm">{task.title}</h3>
            </div>
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="icon" className="h-8 w-8">
                  <MoreHorizontal className="h-4 w-4" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuLabel>Task Actions</DropdownMenuLabel>
                <DropdownMenuSeparator />

                {/* Status change options */}
                {getStatusChangeOptions().map((option) => (
                  <DropdownMenuItem 
                    key={option.value}
                    onClick={() => onStatusChange(task.id, option.value)}
                  >
                    {option.label}
                  </DropdownMenuItem>
                ))}

                <DropdownMenuSeparator />
                <DropdownMenuItem onClick={() => onEdit(task.id)}>
                  <Edit className="mr-2 h-4 w-4" />
                  Edit
                </DropdownMenuItem>
                <DropdownMenuItem 
                  onClick={() => onDelete(task.id)}
                  className="text-red-600 hover:text-red-700 focus:text-red-700"
                >
                  <Trash2 className="mr-2 h-4 w-4" />
                  Delete
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </CardHeader>

        <CardContent className="p-3 pt-2">
          {/* Show description preview or full description based on expanded state */}
          {task.description && (
            <p className={`text-gray-600 text-xs ${!isExpanded ? 'line-clamp-2' : ''}`}>
              {task.description}
            </p>
          )}

          {/* Show "Show more" button if description is long */}
          {task.description && task.description.length > 100 && (
            <Button 
              variant="link" 
              size="sm" 
              className="p-0 h-auto text-xs mt-1"
              onClick={() => setIsExpanded(!isExpanded)}
            >
              {isExpanded ? 'Show less' : 'Show more'}
            </Button>
          )}
        </CardContent>

        <CardFooter className="p-3 pt-0 flex flex-wrap gap-2 items-center">
          {/* Priority badge */}
          <Badge className={cn('text-white', priorityColors[task.priority] || priorityColors[TaskPriority.NONE])}>
            {task.priority || 'Unknown'}
          </Badge>

          {/* Category badge */}
          {task.category && (
            <Badge variant="outline" className="text-xs flex items-center gap-1">
              <Tag className="h-3 w-3" />
              {task.category}
            </Badge>
          )}

          {/* Due date badge */}
          {task.due_date && (
            <Badge variant="outline" className="text-xs flex items-center gap-1">
              <Calendar className="h-3 w-3" />
              {(() => {
                try {
                  return new Date(task.due_date).toLocaleDateString();
                } catch (e) {
                  console.error('Invalid date format:', task.due_date);
                  return 'Invalid date';
                }
              })()}
            </Badge>
          )}

          {/* Contact badge */}
          {task.contact_id && (
            <Badge variant="outline" className="text-xs flex items-center gap-1">
              <User className="h-3 w-3" />
              Contact
            </Badge>
          )}

          {/* AI generated badge */}
          {task.ai_generated && (
            <Badge variant="secondary" className="text-xs">
              AI
            </Badge>
          )}

          {/* Business impact indicator */}
          {task.business_impact !== null && (
            <div className="ml-auto text-xs text-gray-500 flex items-center">
              Impact: 
              <span className="font-medium ml-1">
                {task.business_impact}/10
              </span>
            </div>
          )}
        </CardFooter>
      </Card>
    </div>
  );
}

'use client';

import { useState, useRef } from 'react';
import { useSwipeable } from 'react-swipeable';
import { format } from 'date-fns';
import { 
  Calendar, 
  Tag, 
  User, 
  Edit, 
  Trash2, 
  CheckCircle2,
  XCircle,
  ArrowLeft,
  Clock,
  BarChart
} from 'lucide-react';
import { Task } from './TaskCard';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card } from '@/components/ui/card';
import { Separator } from '@/components/ui/separator';

interface TaskDetailViewProps {
  task: Task;
  onClose: () => void;
  onEdit: (taskId: string) => void;
  onDelete: (taskId: string) => void;
  onToggleComplete: (taskId: string, completed: boolean) => void;
}

export function TaskDetailView({
  task,
  onClose,
  onEdit,
  onDelete,
  onToggleComplete
}: TaskDetailViewProps) {
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const swipeableRef = useRef<HTMLDivElement>(null);

  // Format due date if present
  const formattedDueDate = task.due_date ? (() => {
    try {
      return format(new Date(task.due_date), 'PPPP');
    } catch (e) {
      return null;
    }
  })() : null;

  // Format created/updated dates
  const formattedCreatedAt = task.created_at ? (() => {
    try {
      return format(new Date(task.created_at), 'PPp');
    } catch (e) {
      return null;
    }
  })() : null;

  const formattedUpdatedAt = task.updated_at ? (() => {
    try {
      return format(new Date(task.updated_at), 'PPp');
    } catch (e) {
      return null;
    }
  })() : null;

  // Set up swipe handlers for mobile
  const { ref: swipeRef, ...swipeHandlers } = useSwipeable({
    onSwipedLeft: () => {
      // Swipe left to delete
      setShowDeleteConfirm(true);
    },
    onSwipedRight: () => {
      // Swipe right to toggle complete
      onToggleComplete(task.id, task.status !== 'done');
    },
    trackMouse: false,
    trackTouch: true
  });

  // Priority color
  const getPriorityColor = () => {
    switch (task.priority) {
      case 'high':
        return 'text-red-600';
      case 'medium':
        return 'text-yellow-600';
      case 'low':
        return 'text-blue-600';
      default:
        return 'text-gray-600';
    }
  };

  // Status badge
  const getStatusBadge = () => {
    switch (task.status) {
      case 'done':
        return <Badge className="bg-green-500">Completed</Badge>;
      case 'in-progress':
        return <Badge className="bg-blue-500">In Progress</Badge>;
      case 'todo':
        return <Badge className="bg-gray-500">To Do</Badge>;
      default:
        return <Badge>{task.status}</Badge>;
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4 backdrop-blur-sm">
      <Card className="bg-white rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          {/* Header with close button */}
          <div className="flex justify-between items-center mb-6">
            <Button
              variant="ghost"
              size="icon"
              onClick={onClose}
              className="h-8 w-8"
            >
              <ArrowLeft className="h-5 w-5" />
            </Button>
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => onEdit(task.id)}
                className="flex items-center"
              >
                <Edit className="mr-1 h-4 w-4" />
                Edit
              </Button>
              {!showDeleteConfirm ? (
                <Button
                  variant="destructive"
                  size="sm"
                  onClick={() => setShowDeleteConfirm(true)}
                  className="flex items-center"
                >
                  <Trash2 className="mr-1 h-4 w-4" />
                  Delete
                </Button>
              ) : (
                <div className="flex items-center gap-1">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setShowDeleteConfirm(false)}
                  >
                    Cancel
                  </Button>
                  <Button
                    variant="destructive"
                    size="sm"
                    onClick={() => onDelete(task.id)}
                  >
                    Confirm
                  </Button>
                </div>
              )}
            </div>
          </div>

          {/* Swipeable content area */}
          <div 
            ref={(node) => {
              swipeableRef.current = node;
              // Attach swipeable's ref as well
              if (typeof swipeRef === 'function') {
                swipeRef(node);
              }
            }}
            {...swipeHandlers}
            className="touch-pan-y"
          >
            {/* Title and status */}
            <div className="mb-6">
              <div className="flex items-center justify-between mb-2">
                <h1 className="text-2xl font-bold text-gray-900">{task.title}</h1>
                {getStatusBadge()}
              </div>

              {/* Complete/Uncomplete button */}
              <Button
                variant={task.status === 'done' ? 'destructive' : 'default'}
                className={`w-full mt-4 ${task.status === 'done' ? 'bg-red-100 text-red-700 hover:bg-red-200 border-red-200' : 'bg-green-500 hover:bg-green-600'}`}
                onClick={() => onToggleComplete(task.id, task.status !== 'done')}
              >
                {task.status === 'done' ? (
                  <>
                    <XCircle className="mr-2 h-4 w-4" />
                    Mark as Incomplete
                  </>
                ) : (
                  <>
                    <CheckCircle2 className="mr-2 h-4 w-4" />
                    Mark as Complete
                  </>
                )}
              </Button>
            </div>

            <Separator className="my-4" />

            {/* Description */}
            {task.description && (
              <div className="mb-6">
                <h2 className="text-sm font-medium text-gray-700 mb-2">Description</h2>
                <p className="text-gray-600 whitespace-pre-wrap">{task.description}</p>
              </div>
            )}

            {/* Metadata */}
            <div className="space-y-4">
              {/* Priority */}
              <div className="flex items-center">
                <div className="w-24 text-sm font-medium text-gray-700">Priority:</div>
                <div className={`font-medium ${getPriorityColor()}`}>
                  {task.priority?.charAt(0).toUpperCase() + task.priority?.slice(1) || 'None'}
                </div>
              </div>

              {/* Category */}
              <div className="flex items-center">
                <div className="w-24 text-sm font-medium text-gray-700">Category:</div>
                {task.category ? (
                  <Badge variant="outline" className="flex items-center gap-1">
                    <Tag className="h-3 w-3" />
                    {task.category}
                  </Badge>
                ) : (
                  <span className="text-gray-500">None</span>
                )}
              </div>

              {/* Due date */}
              <div className="flex items-center">
                <div className="w-24 text-sm font-medium text-gray-700">Due date:</div>
                {formattedDueDate ? (
                  <Badge variant="outline" className="flex items-center gap-1">
                    <Calendar className="h-3 w-3" />
                    {formattedDueDate}
                  </Badge>
                ) : (
                  <span className="text-gray-500">None</span>
                )}
              </div>

              {/* Contact */}
              {task.contact_id && (
                <div className="flex items-center">
                  <div className="w-24 text-sm font-medium text-gray-700">Contact:</div>
                  <Badge variant="outline" className="flex items-center gap-1">
                    <User className="h-3 w-3" />
                    Contact ID: {task.contact_id}
                  </Badge>
                </div>
              )}

              {/* Business impact */}
              {task.business_impact !== null && (
                <div className="flex items-center">
                  <div className="w-24 text-sm font-medium text-gray-700">Impact:</div>
                  <Badge variant="outline" className="flex items-center gap-1">
                    <BarChart className="h-3 w-3" />
                    {task.business_impact}/10
                  </Badge>
                </div>
              )}
            </div>

            <Separator className="my-4" />

            {/* Timestamps */}
            <div className="text-xs text-gray-500 space-y-1">
              {formattedCreatedAt && (
                <div className="flex items-center">
                  <Clock className="h-3 w-3 mr-1" />
                  Created: {formattedCreatedAt}
                </div>
              )}
              {formattedUpdatedAt && formattedUpdatedAt !== formattedCreatedAt && (
                <div className="flex items-center">
                  <Clock className="h-3 w-3 mr-1" />
                  Updated: {formattedUpdatedAt}
                </div>
              )}
              {task.ai_generated && (
                <div className="flex items-center mt-1">
                  <Badge variant="secondary" className="text-xs">AI Generated</Badge>
                </div>
              )}
            </div>

            {/* Mobile swipe hint */}
            <div className="mt-8 text-center text-xs text-gray-400 md:hidden">
              <p>Swipe right to mark as {task.status === 'done' ? 'incomplete' : 'complete'}</p>
              <p>Swipe left to delete</p>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}

/**
 * TaskForm - Task Creation & Editing Form Component
 * 
 * This component handles the UI and form logic for creating and editing tasks in the CodexCRM app.
 * It integrates with react-hook-form and Zod for form validation and handles the conversion
 * between frontend camelCase properties and backend snake_case database fields.
 * 
 * Key features:
 * - Form validation with Zod schema
 * - Task property editing (title, notes, status, priority, category, etc.)
 * - Date picker for task due dates
 * - Contact selection for assigning tasks
 * - Support for both create and edit modes
 * - Proper handling of enum values (TaskStatus, TaskPriority, TaskCategory)
 * 
 * The component accepts snake_case task data (TaskOutput) from tRPC and converts it to
 * camelCase for the form, then back to the appropriate format for submission. This ensures
 * type safety throughout the application while maintaining the database structure.
 * 
 * Parent components: 
 * - ThingsMainContent (used in modals for task creation/editing)
 * - TaskDetailView (potentially used for inline editing)
 * 
 * Date: June 11, 2025
 */

'use client';

import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { Calendar } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/form';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { Calendar as CalendarComponent } from '@/components/ui/calendar';
import { format } from 'date-fns';
import { cn } from '@/lib/utils';
import { type AppRouter } from '@codexcrm/server/src/root';
import { type inferRouterOutputs } from '@trpc/server';
import { TaskStatus, TaskPriority, TaskCategory } from '@codexcrm/db/src/models/Task';

type RouterOutput = inferRouterOutputs<AppRouter>;
// This will be the type of a single task object returned by the `tasks.list` procedure
export type TaskOutput = RouterOutput['tasks']['list'][number];

// The actual shape of task data from the API
interface TaskData {
  id: string;
  title: string;
  notes?: string;
  status: TaskStatus;
  priority: TaskPriority;
  category: TaskCategory;
  due_date?: string | null;
  contact_id?: string | null;
  // Other fields omitted for brevity
}

// Form schema
const taskFormSchema = z.object({
  title: z.string().min(1, 'Title is required'),
  notes: z.string().optional(),
  status: z.nativeEnum(TaskStatus).optional(),
  priority: z.nativeEnum(TaskPriority).optional(),
  category: z.nativeEnum(TaskCategory).optional(),
  color: z.string().optional(),
  dueDate: z.date().optional().nullable(),
  contactId: z.string().optional().nullable(),
  business_impact: z.number().optional().nullable(),
  ai_generated: z.boolean().optional(),
});

export type TaskFormValues = z.infer<typeof taskFormSchema>;

export interface TaskFormProps {
  initialData?: TaskOutput | null;
  onSubmit: (data: TaskFormValues) => void;
  onClose: () => void;
  isSubmitting: boolean;
  error?: string | null;
  categories?: string[];
}

export function TaskForm({
  onSubmit,
  initialData,
  onClose,
  isSubmitting,
  error,
  categories = [],
}: TaskFormProps) {
  // Initialize form with default values or initial data
  // Create a properly typed version of initialData to satisfy TypeScript
  const safeInitialData = initialData as unknown as TaskData | null;

  const form = useForm<TaskFormValues>({
    resolver: zodResolver(taskFormSchema),
    defaultValues: safeInitialData
      ? {
          title: safeInitialData.title,
          notes: safeInitialData.notes || '',
          status: safeInitialData.status,
          priority: safeInitialData.priority,
          category: safeInitialData.category || TaskCategory.INBOX,
          dueDate: safeInitialData.due_date ? new Date(safeInitialData.due_date) : null,
          contactId: safeInitialData.contact_id || null,
          // Form-only fields not in the DB model yet
          color: '',
          business_impact: null,
          ai_generated: false,
        }
      : {
          title: '',
          notes: '',
          status: TaskStatus.TODO,
          priority: TaskPriority.NONE,
          category: TaskCategory.INBOX,
          color: '',
          dueDate: null,
          contactId: null,
          business_impact: null,
          ai_generated: false,
        },
  });

  // Status options
  const statusOptions = [
    { value: TaskStatus.TODO, label: 'To Do' },
    { value: TaskStatus.IN_PROGRESS, label: 'In Progress' },
    { value: TaskStatus.DONE, label: 'Done' },
    { value: TaskStatus.CANCELED, label: 'Canceled' },
  ];

  // Priority options
  const priorityOptions = [
    { value: TaskPriority.HIGH, label: 'High' },
    { value: TaskPriority.MEDIUM, label: 'Medium' },
    { value: TaskPriority.LOW, label: 'Low' },
    { value: TaskPriority.NONE, label: 'None' },
  ];

  // Color options
  const colorOptions = [
    { value: '#e2e8f0', label: 'Default' },
    { value: '#f87171', label: 'Red' },
    { value: '#fb923c', label: 'Orange' },
    { value: '#facc15', label: 'Yellow' },
    { value: '#4ade80', label: 'Green' },
    { value: '#60a5fa', label: 'Blue' },
    { value: '#a78bfa', label: 'Purple' },
    { value: '#f472b6', label: 'Pink' },
  ];

  // Business impact options
  const businessImpactOptions = [
    { value: 1, label: '1 - Very Low' },
    { value: 2, label: '2' },
    { value: 3, label: '3' },
    { value: 4, label: '4' },
    { value: 5, label: '5 - Medium' },
    { value: 6, label: '6' },
    { value: 7, label: '7' },
    { value: 8, label: '8' },
    { value: 9, label: '9' },
    { value: 10, label: '10 - Very High' },
  ];

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
        {/* Title */}
        <FormField
          control={form.control}
          name="title"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Title</FormLabel>
              <FormControl>
                <Input placeholder="Task title" {...field} />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Description */}
        <FormField
          control={form.control}
          name="notes"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Description</FormLabel>
              <FormControl>
                <Textarea
                  placeholder="Task description"
                  className="min-h-[100px]"
                  {...field}
                  value={field.value || ''}
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Status */}
        <FormField
          control={form.control}
          name="status"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Status</FormLabel>
              <Select
                onValueChange={field.onChange}
                defaultValue={field.value}
              >
                <FormControl>
                  <SelectTrigger>
                    <SelectValue placeholder="Select status" />
                  </SelectTrigger>
                </FormControl>
                <SelectContent>
                  {statusOptions.map((option) => (
                    <SelectItem key={option.value} value={option.value}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Priority */}
        <FormField
          control={form.control}
          name="priority"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Priority</FormLabel>
              <Select
                onValueChange={field.onChange}
                defaultValue={field.value}
              >
                <FormControl>
                  <SelectTrigger>
                    <SelectValue placeholder="Select priority" />
                  </SelectTrigger>
                </FormControl>
                <SelectContent>
                  {priorityOptions.map((option) => (
                    <SelectItem key={option.value} value={option.value}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Category */}
        <FormField
          control={form.control}
          name="category"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Category</FormLabel>
              <Select
                onValueChange={(value) => field.onChange(value === 'none-category' ? null : value)}
                value={field.value || 'none-category'}
              >
                <FormControl>
                  <SelectTrigger>
                    <SelectValue placeholder="Select category" />
                  </SelectTrigger>
                </FormControl>
                <SelectContent>
                  <SelectItem value={TaskCategory.INBOX}>Inbox</SelectItem>
                  <SelectItem value={TaskCategory.TODAY}>Today</SelectItem>
                  <SelectItem value={TaskCategory.UPCOMING}>Upcoming</SelectItem>
                  <SelectItem value={TaskCategory.ANYTIME}>Anytime</SelectItem>
                  <SelectItem value={TaskCategory.SOMEDAY}>Someday</SelectItem>
                </SelectContent>
              </Select>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Color */}
        <FormField
          control={form.control}
          name="color"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Color</FormLabel>
              <Select
                onValueChange={(value) => field.onChange(value || '#e2e8f0')}
                value={field.value || '#e2e8f0'}
              >
                <FormControl>
                  <SelectTrigger>
                    <SelectValue placeholder="Select color">
                      <div className="flex items-center">
                        <div
                          className="w-4 h-4 rounded-full mr-2"
                          style={{ backgroundColor: field.value || '#e2e8f0' }}
                        />
                        {colorOptions.find(c => c.value === field.value)?.label || 'Default'}
                      </div>
                    </SelectValue>
                  </SelectTrigger>
                </FormControl>
                <SelectContent>
                  {colorOptions.map((option) => (
                    <SelectItem key={option.value} value={option.value}>
                      <div className="flex items-center">
                        <div
                          className="w-4 h-4 rounded-full mr-2"
                          style={{ backgroundColor: option.value }}
                        />
                        {option.label}
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Due Date */}
        <FormField
          control={form.control}
          name="dueDate"
          render={({ field }) => (
            <FormItem className="flex flex-col">
              <FormLabel>Due Date</FormLabel>
              <Popover>
                <PopoverTrigger asChild>
                  <FormControl>
                    <Button
                      variant={"outline"}
                      className={cn(
                        "w-full pl-3 text-left font-normal",
                        !field.value && "text-muted-foreground"
                      )}
                    >
                      {field.value ? (
                        format(field.value, "PPP")
                      ) : (
                        <span>Pick a date</span>
                      )}
                      <Calendar className="ml-auto h-4 w-4 opacity-50" />
                    </Button>
                  </FormControl>
                </PopoverTrigger>
                <PopoverContent className="w-auto p-0" align="start">
                  <CalendarComponent
                    mode="single"
                    selected={field.value || undefined}
                    onSelect={field.onChange}
                    initialFocus
                  />
                </PopoverContent>
              </Popover>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Business Impact */}
        <FormField
          control={form.control}
          name="business_impact"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Business Impact (1-10)</FormLabel>
              <Select
                onValueChange={(value) => {
                  try {
                    field.onChange(value === 'none' ? null : parseInt(value, 10));
                  } catch (e) {
                    console.error('Invalid number format:', value);
                    field.onChange(null);
                  }
                }}
                value={field.value?.toString() || 'none'}
              >
                <FormControl>
                  <SelectTrigger>
                    <SelectValue placeholder="Select business impact" />
                  </SelectTrigger>
                </FormControl>
                <SelectContent>
                  <SelectItem value="none-impact">None</SelectItem>
                  {businessImpactOptions.map((option) => (
                    <SelectItem key={option.value} value={option.value.toString()}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <FormDescription>
                Rate the business impact of this task from 1 (lowest) to 10 (highest)
              </FormDescription>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Error message */}
        {error && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
            {error}
          </div>
        )}

        {/* Form actions */}
        <div className="flex justify-end space-x-2">
          <Button
            type="button"
            variant="outline"
            onClick={onClose}
            disabled={isSubmitting}
          >
            Cancel
          </Button>
          <Button type="submit" disabled={isSubmitting}>
            {isSubmitting ? 'Saving...' : initialData ? 'Update Task' : 'Create Task'}
          </Button>
        </div>
      </form>
    </Form>
  );
}

'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import { 
  DndContext, 
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
  DragEndEvent
} from '@dnd-kit/core';
import { 
  arrayMove,
  SortableContext, 
  sortableKeyboardCoordinates,
  verticalListSortingStrategy
} from '@dnd-kit/sortable';
import { Task } from './TaskCard';
import { TaskCard, TaskStatus, TaskPriority } from './TaskCard';
import { usePathname, useSearchParams } from 'next/navigation';
import { createRoute } from '@/lib/utils/routes';
import type { Route } from 'next';
import { api } from '@/lib/trpc/client';
import { Button } from '@/components/ui/button';
import { Plus, Filter } from 'lucide-react';
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';

interface TasksTableClientProps {
  initialTasks: Task[];
}

export default function TasksTableClient({ initialTasks }: TasksTableClientProps) {
  const [tasks, setTasks] = useState<Task[]>(initialTasks);
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [priorityFilter, setPriorityFilter] = useState<string>('all');
  const [activeTab, setActiveTab] = useState('all');

  const router = useRouter();
  const pathname = usePathname();
  const searchParams = useSearchParams();

  // Set up DnD sensors
  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  // TRPC mutations - commented out until tRPC router is implemented
  // For now, we'll just update the local state
  /*
  const updateTaskMutation = api.tasks.update.useMutation({
    onSuccess: () => {
      router.refresh();
    },
  });

  const deleteTaskMutation = api.tasks.delete.useMutation({
    onSuccess: () => {
      router.refresh();
    },
  });
  */

  // Handle task editing
  const handleEditTask = (taskId: string) => {
    router.push(`/tasks/edit/${taskId}` as Route);
  };

  // Handle task deletion
  const handleDeleteTask = (taskId: string) => {
    // Temporarily just update the local state until tRPC is implemented
    // deleteTaskMutation.mutate({ id: taskId });
    setTasks(tasks.filter(task => task.id !== taskId));
  };

  // Handle status change
  const handleStatusChange = (taskId: string, newStatus: TaskStatus) => {
    // Temporarily just update the local state until tRPC is implemented
    // updateTaskMutation.mutate({ id: taskId, status: newStatus });

    setTasks(tasks.map(task => 
      task.id === taskId ? { ...task, status: newStatus } : task
    ));
  };

  // Handle drag end for reordering tasks
  const handleDragEnd = (event: DragEndEvent) => {
    const { active, over } = event;

    if (over && active.id !== over.id) {
      setTasks((items) => {
        const oldIndex = items.findIndex(item => item.id === active.id);
        const newIndex = items.findIndex(item => item.id === over.id);

        const reorderedTasks = arrayMove(items, oldIndex, newIndex);

        // Update positions in database - commented out until tRPC is implemented
        /*
        reorderedTasks.forEach((task, index) => {
          updateTaskMutation.mutate({ 
            id: task.id, 
            position: index 
          });
        });
        */

        return reorderedTasks;
      });
    }
  };

  // Filter tasks based on search, status, and priority
  const filteredTasks = tasks.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         (task.description?.toLowerCase().includes(searchQuery.toLowerCase()) ?? false);

    const matchesStatus = statusFilter === 'all' || task.status === statusFilter;
    const matchesPriority = priorityFilter === 'all' || task.priority === priorityFilter;

    // Filter by tab (view)
    if (activeTab === 'all') {
      return matchesSearch && matchesStatus && matchesPriority;
    } else if (activeTab === 'today') {
      const today = new Date();
      today.setHours(0, 0, 0, 0);
      const tomorrow = new Date(today);
      tomorrow.setDate(tomorrow.getDate() + 1);

      const isDueToday = task.due_date && 
        new Date(task.due_date) >= today && 
        new Date(task.due_date) < tomorrow;

      return matchesSearch && matchesStatus && matchesPriority && isDueToday;
    } else if (activeTab === 'upcoming') {
      const tomorrow = new Date();
      tomorrow.setHours(0, 0, 0, 0);
      tomorrow.setDate(tomorrow.getDate() + 1);

      const isUpcoming = task.due_date && new Date(task.due_date) >= tomorrow;
      return matchesSearch && matchesStatus && matchesPriority && isUpcoming;
    }

    return matchesSearch && matchesStatus && matchesPriority;
  });

  return (
    <div className="space-y-4">
      <div className="flex flex-col sm:flex-row justify-between gap-4">
        <div className="flex-1">
          <Input
            placeholder="Search tasks..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="max-w-md"
          />
        </div>
        <div className="flex flex-wrap gap-2">
          <Select value={statusFilter} onValueChange={setStatusFilter}>
            <SelectTrigger className="w-[180px]">
              <SelectValue placeholder="Filter by status" />
            </SelectTrigger>
            <SelectContent>
              <SelectGroup>
                <SelectItem value="all">All Statuses</SelectItem>
                <SelectItem value={TaskStatus.TODO}>To Do</SelectItem>
                <SelectItem value={TaskStatus.IN_PROGRESS}>In Progress</SelectItem>
                <SelectItem value={TaskStatus.DONE}>Done</SelectItem>
              </SelectGroup>
            </SelectContent>
          </Select>

          <Select value={priorityFilter} onValueChange={setPriorityFilter}>
            <SelectTrigger className="w-[180px]">
              <SelectValue placeholder="Filter by priority" />
            </SelectTrigger>
            <SelectContent>
              <SelectGroup>
                <SelectItem value="all">All Priorities</SelectItem>
                <SelectItem value={TaskPriority.HIGH}>High</SelectItem>
                <SelectItem value={TaskPriority.MEDIUM}>Medium</SelectItem>
                <SelectItem value={TaskPriority.LOW}>Low</SelectItem>
              </SelectGroup>
            </SelectContent>
          </Select>

          <Button onClick={() => router.push('/tasks/new' as Route)}>
            <Plus className="mr-2 h-4 w-4" />
            New Task
          </Button>
        </div>
      </div>

      <Tabs defaultValue="all" value={activeTab} onValueChange={setActiveTab}>
        <TabsList>
          <TabsTrigger value="all">All Tasks</TabsTrigger>
          <TabsTrigger value="today">Today</TabsTrigger>
          <TabsTrigger value="upcoming">Upcoming</TabsTrigger>
        </TabsList>

        <TabsContent value={activeTab} className="mt-4">
          <Card>
            <CardHeader>
              <CardTitle>
                {activeTab === 'all' ? 'All Tasks' : 
                 activeTab === 'today' ? 'Today\'s Tasks' : 'Upcoming Tasks'}
              </CardTitle>
              <CardDescription>
                {filteredTasks.length} {filteredTasks.length === 1 ? 'task' : 'tasks'} found
              </CardDescription>
            </CardHeader>
            <CardContent>
              <DndContext
                sensors={sensors}
                collisionDetection={closestCenter}
                onDragEnd={handleDragEnd}
              >
                <SortableContext
                  items={filteredTasks.map(task => task.id)}
                  strategy={verticalListSortingStrategy}
                >
                  <div className="space-y-3">
                    {filteredTasks.length > 0 ? (
                      filteredTasks.map((task) => (
                        <TaskCard
                          key={task.id}
                          task={task}
                          onEdit={handleEditTask}
                          onDelete={handleDeleteTask}
                          onStatusChange={handleStatusChange}
                        />
                      ))
                    ) : (
                      <div className="text-center py-8 text-gray-500">
                        No tasks found. Create a new task to get started.
                      </div>
                    )}
                  </div>
                </SortableContext>
              </DndContext>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { format } from 'date-fns';
import { 
  Calendar, 
  Tag, 
  MoreHorizontal, 
  Circle, 
  Check,
  ChevronRight,
  ChevronDown
} from 'lucide-react';
import { Task } from './TaskCard';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';

interface ThingsTaskCardProps {
  task: Task;
  onEdit: (taskId: string) => void;
  onDelete: (taskId: string) => void;
  onStatusChange: (taskId: string, newStatus: string) => void;
  onToggleComplete: (taskId: string, completed: boolean) => void;
}

export function ThingsTaskCard({ 
  task, 
  onEdit, 
  onDelete, 
  onStatusChange,
  onToggleComplete
}: ThingsTaskCardProps) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [isHovered, setIsHovered] = useState(false);
  const isCompleted = task.status === 'done';

  // Set up sortable functionality
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: task.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.5 : 1,
    zIndex: isDragging ? 10 : 1,
  };

  // Format due date if present
  const formattedDueDate = task.due_date ? (() => {
    try {
      return format(new Date(task.due_date), 'MMM d');
    } catch (e) {
      return null;
    }
  })() : null;

  return (
    <div
      ref={setNodeRef}
      style={style}
      {...attributes}
      className="mb-2"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div 
        className={`
          px-3 py-2 rounded-md border border-transparent
          ${isHovered ? 'bg-gray-100' : 'bg-transparent'} 
          ${isCompleted ? 'opacity-60' : 'opacity-100'}
          transition-all duration-150
        `}
      >
        <div className="flex items-start gap-2">
          {/* Checkbox */}
          <Button
            variant="ghost"
            size="sm"
            className="p-0 h-5 w-5 rounded-full hover:bg-transparent"
            onClick={() => onToggleComplete(task.id, !isCompleted)}
          >
            {isCompleted ? (
              <div className="h-5 w-5 rounded-full border border-gray-300 bg-gray-200 flex items-center justify-center">
                <Check className="h-3 w-3 text-gray-600" />
              </div>
            ) : (
              <div className="h-5 w-5 rounded-full border border-gray-300 hover:border-gray-400" />
            )}
          </Button>

          {/* Task Content */}
          <div className="flex-1 min-w-0" {...listeners}>
            <div className="flex items-center justify-between">
              <h3 className={`font-medium text-sm truncate ${isCompleted ? 'line-through text-gray-500' : 'text-gray-900'}`}>
                {task.title}
              </h3>

              {/* Actions (only visible on hover) */}
              {isHovered && (
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" size="icon" className="h-6 w-6 ml-2">
                      <MoreHorizontal className="h-3 w-3" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end" className="w-40">
                    <DropdownMenuItem onClick={() => onEdit(task.id)}>
                      Edit
                    </DropdownMenuItem>
                    <DropdownMenuItem 
                      onClick={() => onStatusChange(task.id, task.status === 'todo' ? 'in-progress' : 'todo')}
                    >
                      {task.status === 'todo' ? 'Move to In Progress' : 'Move to To Do'}
                    </DropdownMenuItem>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem 
                      onClick={() => onDelete(task.id)}
                      className="text-red-600 hover:text-red-700 focus:text-red-700"
                    >
                      Delete
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              )}
            </div>

            {/* Task details (expandable) */}
            {task.description && (
              <div className="mt-1">
                <Button
                  variant="ghost"
                  size="sm"
                  className="p-0 h-5 text-xs text-gray-500 hover:text-gray-700 hover:bg-transparent"
                  onClick={() => setIsExpanded(!isExpanded)}
                >
                  {isExpanded ? (
                    <ChevronDown className="h-3 w-3 mr-1" />
                  ) : (
                    <ChevronRight className="h-3 w-3 mr-1" />
                  )}
                  Notes
                </Button>

                {isExpanded && (
                  <p className="text-xs text-gray-600 mt-1 ml-4 pl-1 border-l-2 border-gray-200">
                    {task.description}
                  </p>
                )}
              </div>
            )}

            {/* Task metadata */}
            <div className="flex flex-wrap items-center gap-2 mt-1">
              {/* Priority indicator */}
              {task.priority && (
                <Badge 
                  variant="outline" 
                  className={`
                    text-xs px-1.5 py-0 h-5 font-normal
                    ${task.priority === 'high' ? 'border-red-200 bg-red-50 text-red-700' : 
                      task.priority === 'medium' ? 'border-yellow-200 bg-yellow-50 text-yellow-700' : 
                      'border-blue-200 bg-blue-50 text-blue-700'}
                  `}
                >
                  {task.priority}
                </Badge>
              )}

              {/* Due date */}
              {formattedDueDate && (
                <Badge 
                  variant="outline" 
                  className="text-xs px-1.5 py-0 h-5 font-normal border-gray-200 bg-gray-50 text-gray-700 flex items-center gap-1"
                >
                  <Calendar className="h-3 w-3" />
                  {formattedDueDate}
                </Badge>
              )}

              {/* Category/Project */}
              {task.category && (
                <Badge 
                  variant="outline" 
                  className="text-xs px-1.5 py-0 h-5 font-normal border-gray-200 bg-gray-50 text-gray-700 flex items-center gap-1"
                >
                  <Tag className="h-3 w-3" />
                  {task.category}
                </Badge>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useRef, useEffect, useState } from 'react';
import { useVirtualizer } from '@tanstack/react-virtual';
import type { VirtualItem } from '@tanstack/react-virtual';
import { motion, AnimatePresence } from 'framer-motion';
import { Task } from './TaskCard';
import { ThingsTaskCard } from './ThingsTaskCard';

interface VirtualizedTaskListProps {
  tasks: Task[];
  onEdit: (taskId: string) => void;
  onDelete: (taskId: string) => void;
  onStatusChange: (taskId: string, newStatus: string) => void;
  onToggleComplete: (taskId: string, completed: boolean) => void;
  isLoading?: boolean;
  emptyState?: React.ReactNode;
}

export function VirtualizedTaskList({
  tasks,
  onEdit,
  onDelete,
  onStatusChange,
  onToggleComplete,
  isLoading = false,
  emptyState
}: VirtualizedTaskListProps) {
  const parentRef = useRef<HTMLDivElement>(null);
  const [parentHeight, setParentHeight] = useState(0);
  const [taskHeights, setTaskHeights] = useState<Record<string, number>>({});
  const [removedTaskIds, setRemovedTaskIds] = useState<string[]>([]);

  // Update parent height on resize
  useEffect(() => {
    if (!parentRef.current) return;

    const updateHeight = () => {
      if (parentRef.current) {
        setParentHeight(parentRef.current.offsetHeight);
      }
    };

    updateHeight();

    const resizeObserver = new ResizeObserver(updateHeight);
    resizeObserver.observe(parentRef.current);

    return () => {
      if (parentRef.current) {
        resizeObserver.unobserve(parentRef.current);
      }
    };
  }, []);

  // Set up virtualizer
  const rowVirtualizer = useVirtualizer({
    count: tasks.length,
    getScrollElement: () => parentRef.current,
    estimateSize: (index: number) => {
      const task = tasks[index];
      return taskHeights[task.id] || 100; // Default height estimate
    },
    overscan: 5,
  });

  // Handle task deletion with animation
  const handleDelete = (taskId: string) => {
    setRemovedTaskIds(prev => [...prev, taskId]);

    // After animation completes, actually delete the task
    setTimeout(() => {
      onDelete(taskId);
      setRemovedTaskIds(prev => prev.filter(id => id !== taskId));
    }, 300);
  };

  // Handle task height measurement
  const measureTaskHeight = (taskId: string, height: number) => {
    if (taskHeights[taskId] !== height) {
      setTaskHeights(prev => ({
        ...prev,
        [taskId]: height
      }));
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-2 max-w-3xl mx-auto">
        {Array.from({ length: 5 }).map((_, index) => (
          <div 
            key={index} 
            className="h-24 bg-gray-100 animate-pulse rounded-md"
          />
        ))}
      </div>
    );
  }

  if (tasks.length === 0 && emptyState) {
    return <>{emptyState}</>;
  }

  return (
    <div 
      ref={parentRef} 
      className="max-w-3xl mx-auto overflow-auto"
      style={{ height: 'calc(100vh - 200px)' }}
    >
      <div
        style={{
          height: `${rowVirtualizer.getTotalSize()}px`,
          width: '100%',
          position: 'relative',
        }}
      >
        <AnimatePresence>
          {rowVirtualizer.getVirtualItems().map((virtualRow: VirtualItem) => {
            const task = tasks[virtualRow.index];
            const isBeingRemoved = removedTaskIds.includes(task.id);

            return (
              <motion.div
                key={task.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ 
                  opacity: isBeingRemoved ? 0 : 1, 
                  y: isBeingRemoved ? -20 : 0,
                  height: isBeingRemoved ? 0 : 'auto'
                }}
                exit={{ opacity: 0, y: -20, height: 0 }}
                transition={{ duration: 0.3 }}
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  transform: `translateY(${virtualRow.start}px)`,
                }}
                onAnimationComplete={() => {
                  // Recalculate virtualizer after animations
                  rowVirtualizer.measure();
                }}
              >
                <div
                  ref={(el) => {
                    if (el) {
                      measureTaskHeight(task.id, el.getBoundingClientRect().height);
                    }
                  }}
                >
                  <ThingsTaskCard
                    task={task}
                    onEdit={onEdit}
                    onDelete={handleDelete}
                    onStatusChange={onStatusChange}
                    onToggleComplete={onToggleComplete}
                  />
                </div>
              </motion.div>
            );
          })}
        </AnimatePresence>
      </div>
    </div>
  );
}

import { Suspense } from 'react';
import { Skeleton } from '@/components/ui/skeleton';
import { createClient } from '@/lib/supabase/server';
import TasksTableClient from './TasksTableClient';
import { TasksWidgets } from '@/components/tasks/TasksWidgets';
import { ThingsTaskCard } from './ThingsTaskCard';
import { TaskForm, TaskFormValues, type TaskOutput } from './TaskForm';
import { TaskPriority, TaskStatus, TaskCategory } from '@codexcrm/db/src/models/Task';
import { CategoryView } from './CategoryViews';
import { DraggableTaskList } from './DraggableTaskList';
import { TaskDetailView } from './TaskDetailView';
import { KeyboardShortcuts } from './KeyboardShortcuts';

// Define the Task interface based on our database schema
export interface Task {
  id: string;
  title: string;
  description: string | null;
  status: TaskStatus;
  priority: TaskPriority;
  category: string | null;
  color: string | null;
  due_date: string | null;
  contact_id: string | null;
  project_id: string | null;
  business_impact: number | null;
  position: number;
  ai_generated: boolean;
  user_id: string;
  created_at?: string;
  updated_at?: string;
}

// A simple loading state for the page content
function TasksPageSkeleton() {
  return (
    <div className="p-4 md:p-6 space-y-6">
      <Skeleton className="h-10 w-full max-w-md" />
      <Skeleton className="h-24 w-full" />
      <Skeleton className="h-96 w-full" />
    </div>
