'use client';

import { Check, Search, User, Mail } from 'lucide-react';
import { useState } from 'react';
import { toast } from 'sonner';

import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Button } from '@/components/ui/button';
import { Checkbox } from '@/components/ui/checkbox';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { api } from '@/lib/trpc';

interface Contact {
  id: string;
  full_name: string;
  email?: string | null;
  company_name?: string | null;
  profile_image_url?: string | null;
}

interface BulkContactSelectorProps {
  groupId: string;
  groupName: string;
  isOpen: boolean;
  onClose: () => void;
}

export function BulkContactSelector({
  groupId,
  groupName,
  isOpen,
  onClose,
}: BulkContactSelectorProps) {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedContactIds, setSelectedContactIds] = useState<string[]>([]);

  // Fetch all contacts
  const { data: allContacts = [], isLoading: contactsLoading } =
    api.contacts.list.useQuery({});

  // Fetch contacts already in this group
  const { data: groupContacts = [] } = api.groups.getContacts.useQuery({
    groupId,
  });

  // Add contacts to group mutation
  const addContactsMutation = api.groups.addContact.useMutation({
    onSuccess: () => {
      toast.success(
        `Added ${selectedContactIds.length} contact(s) to ${groupName}`
      );
      setSelectedContactIds([]);
      onClose();
    },
    onError: (error) => {
      toast.error(`Failed to add contacts: ${error.message}`);
    },
  });

  // Filter contacts not already in group and by search term
  const existingContactIds = new Set(groupContacts.map((c: Contact) => c.id));
  const availableContacts = allContacts
    .filter((contact: Contact) => !existingContactIds.has(contact.id))
    .filter((contact: Contact) => {
      if (!searchTerm) return true;
      const searchLower = searchTerm.toLowerCase();
      return (
        contact.full_name?.toLowerCase().includes(searchLower) ||
        contact.email?.toLowerCase().includes(searchLower) ||
        contact.company_name?.toLowerCase().includes(searchLower)
      );
    }) as Contact[];

  const handleSelectContact = (contactId: string) => {
    setSelectedContactIds((prev) =>
      prev.includes(contactId)
        ? prev.filter((id) => id !== contactId)
        : [...prev, contactId]
    );
  };

  const handleSelectAll = () => {
    if (selectedContactIds.length === availableContacts.length) {
      setSelectedContactIds([]);
    } else {
      setSelectedContactIds(availableContacts.map((c) => c.id));
    }
  };

  const handleAddContacts = async () => {
    for (const contactId of selectedContactIds) {
      await addContactsMutation.mutateAsync({
        contactId,
        groupId,
      });
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-2xl">
        <DialogHeader>
          <DialogTitle>Add Contacts to {groupName}</DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Search */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <Input
              placeholder="Search contacts..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>

          {/* Select All */}
          {availableContacts.length > 0 && (
            <div className="flex items-center space-x-2">
              <Checkbox
                id="select-all"
                checked={selectedContactIds.length === availableContacts.length}
                onCheckedChange={handleSelectAll}
              />
              <label htmlFor="select-all" className="text-sm font-medium">
                Select All ({availableContacts.length} available)
              </label>
            </div>
          )}

          {/* Contact List */}
          <div className="max-h-96 overflow-y-auto space-y-2">
            {contactsLoading ? (
              <div className="text-center py-4">Loading contacts...</div>
            ) : availableContacts.length === 0 ? (
              <div className="text-center py-4 text-gray-500">
                {searchTerm
                  ? 'No contacts found matching your search'
                  : 'All contacts are already in this group'}
              </div>
            ) : (
              availableContacts.map((contact: Contact) => (
                <div
                  key={contact.id}
                  className={`flex items-center space-x-3 p-3 rounded-lg border cursor-pointer hover:bg-gray-50 ${
                    selectedContactIds.includes(contact.id)
                      ? 'bg-purple-50 border-purple-200'
                      : ''
                  }`}
                  onClick={() => handleSelectContact(contact.id)}
                >
                  <Checkbox
                    checked={selectedContactIds.includes(contact.id)}
                    onChange={() => handleSelectContact(contact.id)}
                  />
                  <Avatar className="h-10 w-10">
                    <AvatarImage src={contact.profile_image_url || undefined} />
                    <AvatarFallback>
                      {contact.full_name
                        ?.split(' ')
                        .map((namePart) => namePart[0])
                        .join('')
                        .substring(0, 2)
                        .toUpperCase() || '??'}
                    </AvatarFallback>
                  </Avatar>
                  <div className="flex-1">
                    <div className="font-medium">{contact.full_name}</div>
                    <div className="text-sm text-gray-500 flex items-center gap-2">
                      {contact.email && (
                        <span className="flex items-center gap-1">
                          <Mail className="h-3 w-3" />
                          {contact.email}
                        </span>
                      )}
                      {contact.company_name && (
                        <span> {contact.company_name}</span>
                      )}
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        <DialogFooter>
          <Button variant="outline" onClick={onClose}>
            Cancel
          </Button>
          <Button
            onClick={handleAddContacts}
            disabled={
              selectedContactIds.length === 0 || addContactsMutation.isPending
            }
            className="bg-purple-500 hover:bg-purple-600"
          >
            {addContactsMutation.isPending
              ? 'Adding...'
              : `Add ${selectedContactIds.length} Contact(s)`}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

'use client';

import { useState } from 'react';
import { Loader2 } from 'lucide-react';
import { toast } from 'sonner';

import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { api } from '@/lib/trpc';

// Common emojis for group selection
const POPULAR_EMOJIS = ['', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''];

interface CreateGroupDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function CreateGroupDialog({ open, onOpenChange }: CreateGroupDialogProps) {
  const [newGroupName, setNewGroupName] = useState('');
  const [selectedEmoji, setSelectedEmoji] = useState('');

  // Get the tRPC utils for cache invalidation
  const utils = api.useUtils();

  // Get the create group mutation
  const createGroupMutation = api.groups.save.useMutation({
    onSuccess: (data) => {
      // Invalidate the groups query to refetch the list
      utils.groups.list.invalidate();

      // Show success toast
      toast.success('Group Created', {
        description: `"${newGroupName}" has been created successfully.`,
      });

      // Reset form and close dialog
      setNewGroupName('');
      setSelectedEmoji('');
      onOpenChange(false);
    },
    onError: (error) => {
      console.error('Error creating group:', error);
      toast.error('Failed to Create Group', {
        description: error.message || 'An unexpected error occurred',
      });
    }
  });

  // Handle group creation
  const handleCreateGroup = () => {
    if (!newGroupName.trim()) {
      toast.error('Group name is required');
      return;
    }

    // Call the API to create the group
    createGroupMutation.mutate({
      name: newGroupName.trim(),
      emoji: selectedEmoji
    });
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Create New Group</DialogTitle>
          <DialogDescription>
            Create a new group to organize your contacts. Choose an emoji and enter a name.
          </DialogDescription>
        </DialogHeader>
        <div className="grid gap-4 py-4">
          <div className="flex flex-wrap gap-2 justify-center">
            {POPULAR_EMOJIS.map((emoji) => (
              <Button
                key={emoji}
                variant={selectedEmoji === emoji ? "default" : "outline"}
                className="w-10 h-10 p-0 text-lg"
                onClick={() => setSelectedEmoji(emoji)}
              >
                {emoji}
              </Button>
            ))}
          </div>
          <div className="grid grid-cols-4 items-center gap-4">
            <label htmlFor="name" className="text-right">
              Name
            </label>
            <Input
              id="name"
              value={newGroupName}
              onChange={(e) => setNewGroupName(e.target.value)}
              className="col-span-3"
              placeholder="Enter group name"
            />
          </div>
        </div>
        <DialogFooter>
          <Button variant="outline" onClick={() => onOpenChange(false)}>
            Cancel
          </Button>
          <Button
            onClick={handleCreateGroup}
            disabled={createGroupMutation.isPending || !newGroupName.trim()}
          >
            {createGroupMutation.isPending ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Creating...
              </>
            ) : (
              'Create Group'
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

