'use client';

import { UserCircle } from 'lucide-react';
import Image from 'next/image';
import { useState, useEffect, useMemo } from 'react';

import { api } from '@/lib/trpc';

interface AvatarImageProps {
  src: string | null;
  alt: string;
  size?: 'sm' | 'md' | 'lg' | 'xl';
  className?: string;
}

function getInitials(alt: string): string {
  return alt
    .split(' ')
    .map((word) => word.charAt(0))
    .join('')
    .toUpperCase()
    .slice(0, 2);
}

export function AvatarImage({
  src,
  alt,
  size = 'md',
  className = '',
}: AvatarImageProps) {
  const [imageUrl, setImageUrl] = useState<string | null>(src);
  const [error, setError] = useState(false);

  // Calculate dimensions based on size
  const dimensions = {
    sm: { width: 32, height: 32 },
    md: { width: 40, height: 40 },
    lg: { width: 96, height: 96 },
    xl: { width: 128, height: 128 },
  }[size] || { width: 40, height: 40 };

  const getPathForQuery = (currentSrc: string | null): string => {
    if (!currentSrc) return '';
    // Check if it's a full URL that we might need to parse
    if (currentSrc.startsWith('http')) {
      try {
        const url = new URL(currentSrc);
        const parts = url.pathname.split('/');
        // Assuming path is like /storage/v1/object/public/BUCKET_NAME/ACTUAL_PATH
        // BUCKET_NAME could be 'contact-profile-photo'
        const bucketMarker = 'contact-profile-photo'; 
        const markerIndex = parts.indexOf(bucketMarker);
        if (markerIndex !== -1 && markerIndex < parts.length - 1) {
          return parts.slice(markerIndex + 1).join('/');
        }
        // If it's a URL but doesn't match the expected structure for extraction, 
        // return empty string to disable the query for a signed URL.
        return ''; 
      } catch (e) {
        // Not a valid URL, or some other error during parsing.
        return ''; // Disable query
      }
    }
    // If not starting with http, assume it's already a relative path or an invalid value.
    // The enabled condition below will further filter.
    return currentSrc; 
  };

  const pathForQuery = useMemo(() => getPathForQuery(src), [src]);

  const { data: fileUrlData, isLoading: isLoadingSignedUrl } = api.storage.getFileUrl.useQuery(
    { filePath: pathForQuery }, // Use the processed path
    {
      // Enable if pathForQuery is non-empty, and src (original prop) indicates it's a path that might need signing.
      enabled: !!pathForQuery && !pathForQuery.includes('?token='),
      staleTime: 55 * 60 * 1000, // 55 minutes (URLs valid for 1 hour)
    }
  );

  const actualImageUrl = useMemo(() => {
    if (isLoadingSignedUrl && pathForQuery) {
      return null; // Don't show anything while actively fetching a signed URL for a valid path
    }
    if (fileUrlData?.signedUrl) {
      return fileUrlData.signedUrl; // Use signed URL if available (for private/relative paths)
    }
    // If src is a full URL (http/https) and we didn't get/need a signed URL (e.g. public, or pathForQuery was empty)
    if (src && src.startsWith('http')) {
      return src;
    }
    // If src is not a URL, and we didn't get a signed URL (e.g. it was an invalid relative path or empty)
    // it might be a broken link or meant to be a local placeholder not handled here.
    // For safety, if src is present but no signed URL and not a full URL, return src to see what happens (or null if preferred)
    if (src && !pathForQuery && !src.startsWith('http')) {
       // This case means getPathForQuery returned empty for a non-http src, which implies src itself was deemed not a valid relative path.
       // Or, src was http but didn't match parseable structure.
       // If src is short and not http, it might be an invalid path. If long, could be data URI.
       // Let's return src to allow data URIs or other direct values to pass through if they are not http and not needing signing.
       return src;
    }
    return null; // Fallback: no image or still loading initial src that wasn't a direct URL
  }, [src, fileUrlData, pathForQuery, isLoadingSignedUrl]);

  // Update imageUrl when actualImageUrl changes
  useEffect(() => {
    setImageUrl(actualImageUrl);
    setError(false);
  }, [actualImageUrl]);

  const handleError = () => {
    setError(true);
  };

  return (
    <div
      className={`relative overflow-hidden bg-gray-100 flex items-center justify-center rounded-full ${className}`}
      style={{ width: dimensions.width, height: dimensions.height }}
    >
      {error || !imageUrl ? (
        <div className="flex items-center justify-center h-full w-full">
          {alt ? (
            <span className="font-medium text-gray-600">
              {getInitials(alt)}
            </span>
          ) : (
            <UserCircle
              className="text-gray-400"
              style={{
                width: dimensions.width * 0.7,
                height: dimensions.height * 0.7,
              }}
            />
          )}
        </div>
      ) : imageUrl &&
        (imageUrl.startsWith('http://') || imageUrl.startsWith('https://')) ? (
        <Image
          src={imageUrl}
          alt={alt}
          fill
          sizes={`${dimensions.width}px`}
          className="object-cover"
          onError={handleError}
        />
      ) : (
        <div className="flex items-center justify-center h-full bg-gray-200 text-gray-600 font-medium">
          {getInitials(alt)}
        </div>
      )}
    </div>
  );
}

'use client';

import { UploadCloud, AlertTriangle, Loader2 } from 'lucide-react'; // Added AlertTriangle for errors and Loader2 for processing
import React, { useCallback, useState } from 'react';
import { useDropzone, FileWithPath, FileRejection } from 'react-dropzone';

const TEN_MB_IN_BYTES = 10 * 1024 * 1024;

interface CsvUploadProps {
  onFilesAccepted: (files: FileWithPath[]) => void;
  maxSize?: number; // Optional prop for max file size in bytes
}

export function CsvUpload({
  onFilesAccepted,
  maxSize = TEN_MB_IN_BYTES,
}: CsvUploadProps) {
  const [isHovering, setIsHovering] = useState(false);
  const [rejectionError, setRejectionError] = useState<string | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);

  const onDrop = useCallback(
    (acceptedUploadFiles: FileWithPath[], fileRejections: FileRejection[]) => {
      setIsHovering(false);
      setRejectionError(null); // Clear previous errors
      setIsProcessing(false); // Reset processing state

      if (fileRejections.length > 0) {
        const firstRejection = fileRejections[0];
        if (
          firstRejection.errors.some((err) => err.code === 'file-too-large')
        ) {
          setRejectionError(
            `File is too large. Maximum size is ${maxSize / (1024 * 1024)}MB.`
          );
        } else if (
          firstRejection.errors.some((err) => err.code === 'file-invalid-type')
        ) {
          setRejectionError('Invalid file type. Only .csv files are accepted.');
        } else {
          setRejectionError('File rejected. Please try another file.');
        }
        // Clear accepted files if there were rejections, as we only allow one file
        // and react-dropzone might still populate acceptedFiles if one file is good and another is bad (if multiple=true)
        // Though with multiple=false, this is more of a safeguard.
        onFilesAccepted([]);
        setIsProcessing(false);
        return;
      }

      if (acceptedUploadFiles.length > 0) {
        console.log('Accepted files in CsvUpload:', acceptedUploadFiles);
        setIsProcessing(true);
        // Simulate processing delay
        setTimeout(() => {
          onFilesAccepted(acceptedUploadFiles);
          setIsProcessing(false);
        }, 1500); // Simulate 1.5 seconds processing time
      } else {
        // Handle cases where no files are accepted and no rejections (e.g., user cancels dialog)
        onFilesAccepted([]);
        setIsProcessing(false);
      }
    },
    [onFilesAccepted, maxSize]
  );

  const {
    getRootProps,
    getInputProps,
    isDragActive,
    acceptedFiles,
    fileRejections,
  } = useDropzone({
    onDrop,
    accept: {
      'text/csv': ['.csv'],
    },
    multiple: false,
    maxSize, // Use the maxSize prop
    onDragEnter: () => {
      setIsHovering(true);
      setRejectionError(null);
    }, // Clear error on new drag
    onDragLeave: () => setIsHovering(false),
    onDropAccepted: () => setIsHovering(false), // Handled in onDrop
    // onDropRejected is not strictly needed as onDrop receives fileRejections
  });

  const selectedFile =
    acceptedFiles.length > 0 && !rejectionError && !isProcessing
      ? acceptedFiles[0]
      : null;
  const isError = !!rejectionError;

  return (
    <div
      {...getRootProps()}
      className={`
        border-2 border-dashed rounded-lg p-8 text-center cursor-pointer
        transition-colors duration-200 ease-in-out
        ${
          isError
            ? 'border-destructive bg-destructive/10'
            : isDragActive || isHovering
              ? 'border-primary bg-primary/10'
              : 'border-border hover:border-primary/70'
        }
      `}
    >
      <input {...getInputProps()} />
      <div className="flex flex-col items-center justify-center space-y-2">
        {isError ? (
          <AlertTriangle className="w-12 h-12 mb-2 text-destructive" />
        ) : isProcessing ? (
          <Loader2 className="w-12 h-12 mb-2 text-primary animate-spin" />
        ) : (
          <UploadCloud
            className={`w-12 h-12 mb-2 ${isDragActive || isHovering ? 'text-primary' : 'text-muted-foreground'}`}
          />
        )}

        {isError ? (
          <p className="text-destructive font-semibold">{rejectionError}</p>
        ) : isProcessing ? (
          <p className="text-lg font-semibold text-primary">
            Processing file...
          </p>
        ) : isDragActive ? (
          <p className="text-lg font-semibold text-primary">
            Drop the CSV file here ...
          </p>
        ) : (
          <p className="text-muted-foreground">
            Drag 'n' drop a CSV file here, or click to select file
          </p>
        )}

        {selectedFile && !isDragActive && !isError && !isProcessing && (
          <div className="mt-3 text-sm text-muted-foreground">
            Selected file: {selectedFile.name}
          </div>
        )}
        {!isError && !isProcessing && (
          <p className="text-xs text-muted-foreground mt-2">
            Maximum file size: {maxSize / (1024 * 1024)}MB. Accepted format:
            .csv
          </p>
        )}
      </div>
    </div>
  );
}

'use client';

import { createBrowserClient } from '@supabase/ssr';
import { Loader2, Upload, X, UserCircle } from 'lucide-react';
import Image from 'next/image';
import { useState, useCallback, useEffect } from 'react';
import { useDropzone } from 'react-dropzone';
import { v4 as uuidv4 } from 'uuid';

import { api } from '@/lib/trpc';

interface ImageUploadProps {
  value: string | null;
  onChange: (value: string | null) => void;
  disabled?: boolean;
  contactId?: string;
}

export function ImageUpload({
  value,
  onChange,
  disabled = false,
  contactId,
}: ImageUploadProps) {
  const [isUploading, setIsUploading] = useState(false);
  const [uploadError, setUploadError] = useState<string | null>(null);
  const [previewUrl, setPreviewUrl] = useState<string | null>(value);
  const [imageError, setImageError] = useState(false);

  // Initialize Supabase client
  const supabase = createBrowserClient(
    process.env.NEXT_PUBLIC_SUPABASE_URL || '',
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY || ''
  );

  const getPathForQueryFromValue = (currentValue: string | null): string => {
    if (!currentValue) return '';
    if (currentValue.startsWith('http')) {
      try {
        const url = new URL(currentValue);
        const parts = url.pathname.split('/');
        const bucketMarker = 'contact-profile-photo';
        const markerIndex = parts.indexOf(bucketMarker);
        if (markerIndex !== -1 && markerIndex < parts.length - 1) {
          return parts.slice(markerIndex + 1).join('/');
        }
        return ''; 
      } catch (e) {
        return ''; 
      }
    }
    return currentValue; 
  };

  const pathForFileUrlQuery = getPathForQueryFromValue(value);

  // Generate signed URL for profile photos from Supabase Storage
  const { data: fileUrlData } = api.storage.getFileUrl.useQuery(
    { filePath: pathForFileUrlQuery }, // Use processed path
    {
      enabled: !!pathForFileUrlQuery && !pathForFileUrlQuery.includes('?token='),
      staleTime: 55 * 60 * 1000, // 55 minutes (URLs valid for 1 hour)
    }
  );

  // Update preview URL when signed URL is fetched
  useEffect(() => {
    if (fileUrlData?.signedUrl) {
      setPreviewUrl(fileUrlData.signedUrl);
      setImageError(false);
    }
  }, [fileUrlData]);

  // Update preview URL when value changes
  useEffect(() => {
    // Only clear the preview if value is explicitly null
    // (for initial load, we want to wait for the signed URL)
    if (value === null) {
      setPreviewUrl(null);
    }
    setImageError(false);
  }, [value]);

  // Use tRPC mutation to get signed upload URL
  const getUploadUrlMutation = api.storage.getUploadUrl.useMutation();

  const onDrop = useCallback(
    async (acceptedFiles: File[]) => {
      try {
        if (acceptedFiles.length === 0) return;

        setIsUploading(true);
        setUploadError(null);

        const file = acceptedFiles[0]; // Only use the first file

        // Validate file size (max 2MB for Supabase free tier)
        if (file.size > 2 * 1024 * 1024) {
          throw new Error('File size must be less than 2MB');
        }

        // Validate file type
        if (!file.type.startsWith('image/')) {
          throw new Error('Only image files are allowed');
        }

        // Create unique filename
        const fileExt = file.name.split('.').pop();
        const fileName = `${contactId || uuidv4()}-${Date.now()}.${fileExt}`;

        // Get signed upload URL from backend
        const { signedUrl, path } = await getUploadUrlMutation.mutateAsync({
          fileName,
          contentType: file.type,
          folderPath: 'contacts',
        });

        // Upload file directly to Storage using the signed URL
        const uploadResponse = await fetch(signedUrl, {
          method: 'PUT',
          headers: {
            'Content-Type': file.type,
          },
          body: file,
        });
        if (!uploadResponse.ok) {
          const errorText = await uploadResponse.text();
          const errorMessage = `Upload failed: ${uploadResponse.status} ${uploadResponse.statusText} - ${errorText}`;
          console.error(errorMessage, { status: uploadResponse.status, statusText: uploadResponse.statusText, errorText });
          throw new Error(errorMessage);
        }

        // Get the URL for the file
        const { data: fileData, error: urlError } = await supabase.storage
          .from('contact-profile-photo')
          .createSignedUrl(path, 3600); // URL valid for 1 hour

        if (urlError) {
          let specificMessage = `Error creating signed URL: ${urlError.message}`;
          if (urlError.message?.includes('bucket') || urlError.message?.includes('not found')) {
            specificMessage = 'Storage bucket not set up. Please contact administrator to set up contact photo storage.';
          }
          console.error(specificMessage, urlError);
          throw new Error(specificMessage);
        }

        const publicUrl = supabase.storage
          .from('contact-profile-photo')
          .getPublicUrl(path).data.publicUrl;

        onChange(path); // Store the relative path
        setUploadError(null);
      } catch (error: any) {
        let message = 'An unknown error occurred during upload.';
        if (error instanceof Error) {
          message = error.message;
        } else if (typeof error === 'string') {
          message = error;
        } else if (error && typeof error.message === 'string') {
          message = error.message; // Attempt to get message from object
        }
        console.error('Error during upload process:', message, error);
        setUploadError(message);
        // onChange(null); // Consider if this is desired UX
      } finally {
        setIsUploading(false);
      }
    },
    [getUploadUrlMutation, supabase, onChange, contactId]
  );

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.webp'],
    },
    maxFiles: 1,
    disabled: isUploading || disabled,
  });

  // Delete file mutation
  const deleteFileMutation = api.storage.deleteFile.useMutation();

  const handleRemove = useCallback(
    async (e: React.MouseEvent) => {
      e.stopPropagation();

      // If the value is a storage path, delete the file
      if (value && value.includes('contact-profile-photo')) {
        try {
          // Extract the path part from the URL if needed
          const path = value.includes('?') ? value.split('?')[0] : value;
          await deleteFileMutation.mutateAsync({ filePath: path });
        } catch (error) {
          console.error('Error deleting file:', error);
        }
      }

      setPreviewUrl(null);
      onChange(null);
    },
    [onChange, value, deleteFileMutation]
  );

  const handleImageError = () => {
    setImageError(true);
  };

  return (
    <div className="space-y-2">
      <div
        {...getRootProps()}
        className={`
          relative
          cursor-pointer
          border-2
          border-dashed
          rounded-md
          p-4
          transition
          ${isDragActive ? 'border-blue-500 bg-blue-50' : 'border-gray-300'}
          ${isUploading ? 'opacity-50 cursor-not-allowed' : ''}
          ${
            disabled ? 'opacity-50 cursor-not-allowed' : 'hover:border-blue-400'
          }
        `}
      >
        <input {...getInputProps()} />

        {isUploading ? (
          <div className="flex flex-col items-center justify-center p-4">
            <Loader2 className="h-10 w-10 text-blue-500 animate-spin mb-2" />
            <p className="text-sm text-gray-500">Uploading image...</p>
          </div>
        ) : previewUrl ? (
          <div className="relative flex justify-center">
            <div className="relative h-32 w-32 rounded-full overflow-hidden">
              {imageError ? (
                <div className="flex items-center justify-center h-full w-full bg-gray-100">
                  <UserCircle className="h-16 w-16 text-gray-400" />
                </div>
              ) : (
                <Image
                  src={previewUrl?.startsWith('/') || previewUrl?.startsWith('http') ? previewUrl : `/${previewUrl}`}
                  alt="Contact avatar"
                  fill
                  style={{ objectFit: 'cover' }}
                  sizes="128px"
                  onError={handleImageError}
                />
              )}
            </div>
            {!disabled && (
              <button
                type="button"
                onClick={handleRemove}
                className="absolute top-0 right-0 bg-red-500 text-white p-1 rounded-full hover:bg-red-600 transition-colors"
              >
                <X className="h-4 w-4" />
              </button>
            )}
          </div>
        ) : (
          <div className="flex flex-col items-center justify-center p-4">
            <Upload className="h-10 w-10 text-gray-400 mb-2" />
            <p className="text-sm text-center text-gray-500 mb-1">
              {isDragActive ? 'Drop the image here' : 'Upload a profile photo'}
            </p>
            <p className="text-xs text-center text-gray-400">
              Drag & drop or click to select
              <br />
              Supported formats: JPG, PNG, GIF, WEBP (max 2MB)
            </p>
          </div>
        )}
      </div>

      {uploadError && (
        <p className="text-sm text-red-600 mt-2">{uploadError}</p>
      )}
    </div>
  );
}

import type { User } from '@supabase/supabase-js';

import { createClient } from '@/lib/supabase/client';

const supabase = createClient();

/**
 * Fetches the current authenticated user
 * @returns Object containing user data and any error
 */
export async function fetchCurrentUser(): Promise<{
  user: User | null;
  error: any | null;
}> {
  const {
    data: { user },
    error,
  } = await supabase.auth.getUser();
  return { user, error };
}

/**
 * Updates the current user's password
 * @param password New password to set
 * @returns Object containing any error that occurred
 */
export async function updateUserPassword(
  password: string
): Promise<{ error: any | null }> {
  const { error } = await supabase.auth.updateUser({ password });
  return { error };
}

/**
 * Maps common Supabase error messages to user-friendly messages
 * @param errorMessage Original error message from Supabase
 * @returns User-friendly error message
 */
export function mapAuthErrorMessage(errorMessage: string): string {
  if (errorMessage.includes('Password should be at least')) {
    return 'Password must be at least 6 characters long.';
  }
  if (errorMessage.includes('rate limited')) {
    return 'Too many attempts. Please try again later.';
  }
  // Add more mappings as needed
  return errorMessage;
}

import Papa, { ParseResult, ParseError, ParseLocalConfig } from 'papaparse';

export interface ParsedCsvRow {
  [key: string]: string | number | boolean | null | undefined;
}

export interface ValidatedContactData {
  full_name: string;
  email: string | null;
  phone?: string | null;
  company?: string | null;
  job_title?: string | null;
  website?: string | null;
  notes?: string | null;
  lead_source?: string | null;
  tags?: string | null;
}

export interface ParseCsvFileResult {
  data: ParsedCsvRow[];
  errors: Papa.ParseError[];
  meta: Papa.ParseMeta;
}

/**
 * Parses a CSV file using Papa Parse.
 * @param file The CSV file to parse.
 * @param options Optional Papa Parse configuration.
 * @returns A Promise that resolves with the parsed data, errors, and metadata.
 */
export function parseCsvFile(
  file: File,
  options?: Omit<ParseLocalConfig<ParsedCsvRow, File>, 'complete' | 'error'>
): Promise<ParseCsvFileResult> {
  return new Promise((resolve, reject) => {
    const papaConfig: ParseLocalConfig<ParsedCsvRow, File> = {
      // Default settings that can be overridden by options
      header: true,
      skipEmptyLines: true,
      dynamicTyping: true,
      ...options, // User-provided options override defaults but not complete/error

      // Explicitly defined complete and error for local file parsing
      complete: (results: ParseResult<ParsedCsvRow>, parsedFile: File) => {
        resolve({
          data: results.data,
          errors: results.errors,
          meta: results.meta,
        });
      },
      error: (error: Error, parsedFile: File) => {
        console.error('Error parsing CSV file:', error, parsedFile);
        const err = new Error(error.message);
        // You could attach the original PapaError to the new Error if needed:
        // (err as any).papaErrorDetails = error;
        reject(err);
      },
    };

    Papa.parse(file, papaConfig);
  });
}

