export interface CsvValidationError {
  row?: number; // Row number in the CSV (1-indexed for data rows)
  field?: string;
  message: string;
  type: 'header' | 'data' | 'email_format';
}

const REQUIRED_HEADERS: string[] = ['full_name', 'email'];
const EMAIL_REGEX = /^[^\s@]+@[^\s@]+\.[^\s@]+$/; // Basic email format validation
const URL_REGEX =
  /^(https?:\/\/)?([\da-z.-]+)\.([a-z.]{2,6})([\/\w .-]*)*\/?$/;

export function validateCsvData(parsedResult: ParseCsvFileResult): {
  validatedData: ValidatedContactData[];
  errors: CsvValidationError[];
} {
  const errors: CsvValidationError[] = [];
  const headers = parsedResult.meta.fields;

  if (!headers || headers.length === 0) {
    errors.push({
      message: 'CSV is empty or headers are missing.',
      type: 'header',
    });
    return { validatedData: [], errors }; // Stop validation if no headers
  }

  // 1. Validate Headers
  for (const requiredHeader of REQUIRED_HEADERS) {
    if (!headers.includes(requiredHeader)) {
      errors.push({
        message: `Required header "${requiredHeader}" is missing.`,
        type: 'header',
        field: requiredHeader,
      });
    }
  }

  // If critical header errors exist, perhaps return early
  if (
    errors.some(
      (err) =>
        err.type === 'header' && REQUIRED_HEADERS.includes(err.field || '')
    )
  ) {
    // Optionally, return only header errors if essential headers are missing
    // return errors;
  }

  // 2. Validate Data Rows and Transform Missing Names
  const validatedData: ValidatedContactData[] = [];
  parsedResult.data.forEach((originalRow, index) => {
    const row = { ...originalRow }; // Work on a copy
    const csvRowNumber = index + 2; // PapaParse data is 0-indexed, CSV rows are 1-indexed, +1 for header

    // Trim whitespace from all string values in the row
    for (const key in row) {
      if (typeof row[key] === 'string') {
        row[key] = (row[key] as string).trim();
      }
    }
    let emailIsValid = false;

    // Check for email presence and basic format
    const emailValue = row.email;
    if (
      emailValue === null ||
      emailValue === undefined ||
      String(emailValue).trim() === ''
    ) {
      errors.push({
        row: csvRowNumber,
        field: 'email',
        message: 'Email is missing.',
        type: 'data',
      });
    } else if (
      typeof emailValue === 'string' &&
      !EMAIL_REGEX.test(emailValue.trim())
    ) {
      errors.push({
        row: csvRowNumber,
        field: 'email',
        message: `Invalid email format: "${emailValue}".`,
        type: 'email_format',
      });
    } else {
      emailIsValid = true;
    }

    // Handle full_name
    const fullNameValue = row.full_name;
    if (
      fullNameValue === null ||
      fullNameValue === undefined ||
      String(fullNameValue).trim() === ''
    ) {
      if (emailIsValid && typeof emailValue === 'string') {
        const emailLocalPart = emailValue.split('@')[0];
        const nameParts = emailLocalPart
          .split(/[._-]/) // Split by common separators like '.', '_', '-'
          .map(
            (part) => part.charAt(0).toUpperCase() + part.slice(1).toLowerCase()
          )
          .filter((part) => part !== ''); // Remove empty parts

        if (nameParts.length > 0) {
          row.full_name = nameParts.join(' ');
        } else {
          row.full_name = 'Imported Contact'; // Fallback if email local part is unusual
        }
      } else {
        row.full_name = 'Imported Contact'; // Fallback if email is not valid for derivation
        // Only add an error if email was also missing/invalid, otherwise we've provided a fallback.
        if (!emailIsValid) {
          errors.push({
            row: csvRowNumber,
            field: 'full_name',
            message:
              'Full name is missing and cannot be derived (invalid or missing email).',
            type: 'data',
          });
        }
      }
    }
    // Ensure first_name and last_name are not present on the row object before casting
    delete row.first_name;
    delete row.last_name;

    // Validate website if present
    const websiteValue = row.website;
    if (
      websiteValue !== null &&
      websiteValue !== undefined &&
      String(websiteValue).trim() !== ''
    ) {
      if (
        typeof websiteValue !== 'string' ||
        !URL_REGEX.test(String(websiteValue).trim())
      ) {
        errors.push({
          row: csvRowNumber,
          field: 'website',
          message: `Invalid URL format for website: "${websiteValue}".`,
          type: 'data',
        });
      }
    }

    // After all transformations and validations for the row
    validatedData.push(row as unknown as ValidatedContactData); // Cast to ValidatedContactData after processing
  });

  return { validatedData, errors };
}

// apps/web/src/lib/dateUtils.ts

/**
 * Safely parses a date string or Date object.
 * @param dateInput The date string or Date object.
 * @returns A Date object if parsing is successful, otherwise null.
 */
function safeParseDate(
  dateInput: string | Date | null | undefined
): Date | null {
  if (!dateInput) return null;
  const date = dateInput instanceof Date ? dateInput : new Date(dateInput);
  return isNaN(date.getTime()) ? null : date;
}

/**
 * Formats a date for display (e.g., MM/DD/YYYY, HH:mm).
 * @param dateInput The date string or Date object.
 * @returns Formatted date string or '' if invalid.
 */
export function formatDateForDisplay(
  dateInput: string | Date | null | undefined
): string {
  const date = safeParseDate(dateInput);
  if (!date) return '';

  const _options: Intl.DateTimeFormatOptions = {
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    hour12: false, // Use 24-hour format for consistency with datetime-local
  };
  // Adjust to a common display format, e.g., 07/11/2022, 02:02
  // Intl.DateTimeFormat can be locale-sensitive. For DD/MM/YYYY HH:mm:
  const day = String(date.getDate()).padStart(2, '0');
  const month = String(date.getMonth() + 1).padStart(2, '0'); // Month is 0-indexed
  const year = date.getFullYear();
  const hours = String(date.getHours()).padStart(2, '0');
  const minutes = String(date.getMinutes()).padStart(2, '0');

  return `${day}/${month}/${year}, ${hours}:${minutes}`;
}

/**
 * Formats a date for the value of a datetime-local input (YYYY-MM-DDTHH:mm).
 * @param dateInput The date string or Date object.
 * @returns Formatted date string for input or empty string if invalid.
 */
export function formatDateForInput(
  dateInput: string | Date | null | undefined
): string {
  const date = safeParseDate(dateInput);
  if (!date) return '';

  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0'); // Month is 0-indexed
  const day = String(date.getDate()).padStart(2, '0');
  const hours = String(date.getHours()).padStart(2, '0');
  const minutes = String(date.getMinutes()).padStart(2, '0');

  return `${year}-${month}-${day}T${hours}:${minutes}`;
}

/**
 * Parses a datetime-local input string (YYYY-MM-DDTHH:mm) into a full ISO string.
 * @param inputDateString The date string from datetime-local input.
 * @returns Full ISO string or null if input is invalid/empty.
 */
export function parseInputDateString(
  inputDateString: string | null | undefined
): string | null {
  if (!inputDateString) return null;
  // Input is YYYY-MM-DDTHH:mm. Convert to Date object then to full ISO string.
  const date = new Date(inputDateString);
  if (isNaN(date.getTime())) return null;
  return date.toISOString();
}

/**
 * Formats a date for display in a more user-friendly way, e.g., 'November 7, 2022, 2:02 AM'.
 * Uses Intl.DateTimeFormat for better localization if needed in the future.
 * @param dateInput The date string or Date object.
 * @returns Formatted date string or '' if invalid.
 */
export function formatDateTime(
  dateInput: string | Date | null | undefined
): string {
  const date = safeParseDate(dateInput);
  if (!date) return '';

  return new Intl.DateTimeFormat(undefined, {
    // undefined locale uses browser's default
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    hour: 'numeric',
    minute: '2-digit',
    // timeZoneName: 'short' // Optional: if you want to display timezone
  }).format(date);
}

// apps/web/src/lib/supabase/server.ts

import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { Database } from '@codexcrm/db'

// This is now an async function since cookies() returns a Promise
export async function createClient() {
  // `cookies()` is now async, so we need to await it
  const cookieStore = await cookies()

  return createServerClient<Database>(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
    {
      cookies: {
        getAll() {
          return cookieStore.getAll()
        },
        setAll(cookiesToSet) {
          try {
            cookiesToSet.forEach(({ name, value, options }) => {
              cookieStore.set(name, value, options)
            })
          } catch (error) {
            // The `setAll` method was called from a Server Component.
            // This can be ignored if you have middleware refreshing
            // user sessions, but is kept for safety.
          }
        },
      },
    }
  )
}

// apps/web/middleware.ts

import * as Sentry from '@sentry/nextjs';
import { createServerClient } from '@supabase/ssr';
import { NextResponse, type NextRequest } from 'next/server';

const protectedPaths = ['/', '/dashboard', '/contacts', '/groups'];
const publicOnlyPaths = [
  '/log-in',
  '/sign-up',
  '/forgot-password',
  '/reset-password',
  '/sign-up/confirmation',
];

export async function middleware(request: NextRequest) {
  let response = NextResponse.next({
    request: { headers: request.headers },
  });

  // 1. Use the modern, non-deprecated cookie handling for middleware
  const supabase = createServerClient(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
    {
      cookies: {
        getAll() {
          return request.cookies.getAll();
        },
        // The `setAll` function is required for the new API
        setAll(cookiesToSet) {
          cookiesToSet.forEach(({ name, value, options }) => request.cookies.set(name, value));
          response = NextResponse.next({
            request: { headers: request.headers },
          });
          cookiesToSet.forEach(({ name, value, options }) => response.cookies.set(name, value, options));
        },
      },
    }
  );

  // 2. Use only `getUser()`. It efficiently handles session refresh and is the source of truth.
  const { data: { user } } = await supabase.auth.getUser();

  const { pathname } = request.nextUrl;
  const isProtectedPath = protectedPaths.some(
    (path) => path === '/' ? pathname === path : pathname.startsWith(path)
  );
  const isPublicOnlyPath = publicOnlyPaths.includes(pathname);

  // 3. Handle redirects with focused logging
  if (isProtectedPath && !user) {
    const redirectUrl = new URL('/log-in', request.url);
    Sentry.captureMessage(`Redirecting unauthenticated user from ${pathname} to /log-in`, 'warning');
    return NextResponse.redirect(redirectUrl);
  }

  if (isPublicOnlyPath && user) {
    const redirectUrl = new URL('/dashboard', request.url);
    Sentry.captureMessage(`Redirecting authenticated user (ID: ${user.id}) from ${pathname} to /dashboard`, 'warning');
    return NextResponse.redirect(redirectUrl);
  }

  // If no redirect, return the response. Any updated auth cookies are already set.
  return response;
}

export const config = {
  matcher: [
    /*
     * Match all request paths except for the ones starting with:
     * - api (API routes, e.g., tRPC)
     * - _next/static (static files)
     * - _next/image (image optimization files)
     * - favicon.ico (favicon file)
     * - etc.
     */
    '/((?!api|_next/static|_next/image|favicon.ico|.*\\.(?:svg|png|jpg|jpeg|gif|webp)$).*)',
  ],
};
